import { useState, useEffect } from "react";
import "./App.css";
import MovieBox from "./MovieBox";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Navbar,
  Container,
  Nav,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";

const API_URL =
  "https://api.themoviedb.org/3/movie/popular?api_key=a16776e77d413e17d3d48b4e780334d0&language=fr-FR";

const API_SEARCH="https://api.themoviedb.org/3/search/movie?api_key=a16776e77d413e17d3d48b4e780334d0&query&language=fr-FR";

function App() {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] =useState('');

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => {
        setMovies(data.results);
      });
  }, []);

  const searchMovie = async(e)=>{
    e.preventDefault();
  }

  return (
    <>
      <Navbar bg="dark" expand="lg" variant="dark">
        <Container fluid>
          <Navbar.Brand href="/home">Film infos</Navbar.Brand>
          <Navbar.Brand href="/home">Tendance Ã  la Une!</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll"></Navbar.Toggle>
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-3"
              style={{ maxHeight: "100px" }}
              navbarScroll
            ></Nav>
            <Form className="d-flex" on>
              <FormControl
                type="search"
                placeholder="Film rechercher"
                className="me-2"
                aria-label="search"
                name=""
              ></FormControl>
              <Button variant="secondary" type="submit">
                trouver
              </Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="container">
        <div className="grid">
          {movies.map((movieReq) => (
            <MovieBox key={movieReq.id} {...movieReq} />
          ))}
        </div>
      </div>
    </>
  );
}

export default App;
