import React, { useState, useEffect } from "react";
import "./App.css";
import MovieBox from "./MovieBox";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Navbar,
  Container,
  Nav,
  Form,
  FormControl,
  Button,
  Alert,
  FormCheck,
} from "react-bootstrap";

const API_URL =
  "https://api.themoviedb.org/3/movie/popular?api_key=a16776e77d413e17d3d48b4e780334d0&language=fr-FR";

function App() {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState("");
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);

  useEffect(() => {
    // RÃ©cupÃ©rez les genres depuis l'API
    fetch("https://api.themoviedb.org/3/genre/movie/list?api_key=a16776e77d413e17d3d48b4e780334d0&language=fr-FR")
      .then((res) => res.json())
      .then((data) => {
        setGenres(data.genres);
      });

    // RÃ©cupÃ©rez les films populaires
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
  }, []);

  const searchMovie = async (e) => {
    e.preventDefault();
    console.log("Searching");
    try {
      const url = `https://api.themoviedb.org/3/search/movie?api_key=bcc4ff10c2939665232d75d8bf0ec093&query=${query}&language=fr-FR`;
      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
      setMovies(data.results);
    } catch (e) {
      console.log(e);
    }
  };

  const changeHandler = (e) => {
    setQuery(e.target.value);
  };

  const toggleGenreSelection = (genreId) => {
    if (selectedGenres.includes(genreId)) {
      // Si le genre est dÃ©jÃ  sÃ©lectionnÃ©, le retirez de la liste
      setSelectedGenres(selectedGenres.filter((id) => id !== genreId));
    } else {
      // Sinon, ajoutez-le Ã  la liste
      setSelectedGenres([...selectedGenres, genreId]);
    }
  };

  const filterMoviesByGenre = async () => {
    try {
      const genreQueryParam = selectedGenres.length > 0 ? `with_genres=${selectedGenres.join(",")}` : "";
      const url = `https://api.themoviedb.org/3/discover/movie?api_key=a16776e77d413e17d3d48b4e780334d0&language=fr-FR&${genreQueryParam}`;
      const res = await fetch(url);
      const data = await res.json();
      setMovies(data.results);
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => {
    filterMoviesByGenre();
  }, [selectedGenres]);

  return (
    <>
      <Navbar bg="dark" expand="lg" variant="dark">
        <Container fluid>
          <Navbar.Brand href="/home">Film Infos</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav className="me-auto my-2 my-lg-3" style={{ maxHeight: "100px" }} navbarScroll>
              <div className="genre-filters">
                {genres.map((genre) => (
                  <FormCheck
                    key={genre.id}
                    type="checkbox"
                    id={`genre-checkbox-${genre.id}`}
                    label={genre.name}
                    checked={selectedGenres.includes(genre.id)}
                    onChange={() => toggleGenreSelection(genre.id)}
                  />
                ))}
                <Button
                  variant="outline-secondary"
                  onClick={() => setSelectedGenres([])} // Effacer les sÃ©lections
                >
                  Effacer
                </Button>
              </div>
            </Nav>
            <Form className="d-flex" onSubmit={searchMovie} autoComplete="off">
              <FormControl
                type="search"
                placeholder="Movie Search"
                className="me-2"
                aria-label="search"
                name="query"
                value={query}
                onChange={changeHandler}
              />
              <Button variant="secondary" type="submit">
                Search
              </Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div>
        {movies.length > 0 ? (
          <div className="container">
            <div className="grid">
              {movies.map((movieReq) => (
                <MovieBox key={movieReq.id} {...movieReq} />
              ))}
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-center align-items-center vertical-center">
            <Alert className="text-center">
              DÃ©solÃ©, aucun film ne correspond Ã  votre recherche, relancer la recherche! ðŸ˜…
            </Alert>
          </div>
        )}
      </div>
    </>
  );
}

export default App;
