import React, { useState, useEffect } from "react";
import "./App.css";
import MovieBox from "./MovieBox";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Navbar,
  Container,
  Nav,
  Form,
  FormControl,
  Button,
  Alert,
} from "react-bootstrap";

const API_URL =
  "https://api.themoviedb.org/3/movie/popular?api_key=a16776e77d413e17d3d48b4e780334d0&language=fr-FR";
const API_SEARCH =
  "https://api.themoviedb.org/3/search/movie?api_key=a16776e77d413e17d3d48b4e780334d0&query&language=fr-FR";

function App() {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });
  }, []);

  const searchMovie = async (e) => {
    e.preventDefault();
    console.log("Searching");
    try {
      const url = `https://api.themoviedb.org/3/search/movie?api_key=bcc4ff10c2939665232d75d8bf0ec093&query=${query}&language=fr-FR`;
      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
      setMovies(data.results);
    } catch (e) {
      console.log(e);
    }
  };

  const changeHandler = (e) => {
    setQuery(e.target.value);
  };

  const categories = ["Action", "Comedy", "Drama", "Science Fiction", "Horror"];

  const filterMoviesByCategory = (movies, category) => {
    if (category === "") {
      return movies;
    } else {
      return movies.filter((movie) =>
        movie.genres.some((genre) => genre.name === category)
      );
    }
  };

  const filteredMovies = filterMoviesByCategory(movies, selectedCategory);

  return (
    <>
      <Navbar bg="dark" expand="lg" variant="dark">
        <Container fluid>
          <Navbar.Brand href="/home">Film Infos</Navbar.Brand>
          <Navbar.Brand href="/home">Tendance</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="nabarScroll">
            <Nav
              className="me-auto my-2 my-lg-3"
              style={{ maxHeight: "100px" }}
              navbarScroll
            ></Nav>
            <Form
              className="d-flex"
              onSubmit={searchMovie}
              autoComplete="off"
            >
              <FormControl
                type="search"
                placeholder="Movie Search"
                className="me-2"
                aria-label="search"
                name="query"
                value={query}
                onChange={changeHandler}
              />
              <Button variant="secondary" type="submit">
                Search
              </Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="category-buttons">
        {categories.map((category) => (
          <Button
            key={category}
            variant="outline-primary"
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </Button>
        ))}
        <Button
          variant="outline-primary"
          onClick={() => setSelectedCategory("")}
        >
          Clear
        </Button>
      </div>
      <div>
        {filteredMovies.length > 0 ? (
          <div className="container">
            <div className="grid">
              {filteredMovies.map((movieReq) => (
                <MovieBox key={movieReq.id} {...movieReq} />
              ))}
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-center align-items-center vertical-center">
            <Alert className="text-center">
              Désolé, aucun film ne correspond à votre recherche, relancer la recherche! 😅
            </Alert>
          </div>
        )}
      </div>
    </>
  );
}

export default App;
